import React, { useState, useEffect } from "react";
import axios from "axios";
import Chatbot from "../components/Chatbot";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");
  const [currentPage, setCurrentPage] = useState(0);
  const [chatOpen, setChatOpen] = useState(false);
  const [newDonor, setNewDonor] = useState({
    name: "",
    bloodType: "",
    city: "",
    contact: "",
  });

  useEffect(() => {
    fetchDonors();
  }, []);

  // Fetch donors from backend
  const fetchDonors = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/donors");
      setDonors(response.data);
    } catch (error) {
      console.error("❌ Error fetching donors:", error);
    }
  };

  // Register a new donor
  const handleRegisterDonor = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", newDonor);
      setDonors([...donors, response.data]); // Update state with new donor
      setNewDonor({ name: "", bloodType: "", city: "", contact: "" }); // Reset form
    } catch (error) {
      console.error("❌ Error registering donor:", error);
    }
  };

  // Handle donation click
  const handleDonateClick = async (donorId) => {
    const donationDate = new Date().toISOString(); // Store the current timestamp

    // Update frontend state
    setDonors((prevDonors) =>
      prevDonors.map((donor) =>
        donor._id === donorId
          ? { ...donor, donated: true, donationDate }
          : donor
      )
    );

    // Send donation date to backend
    try {
      await axios.put(`http://localhost:5000/api/donors/${donorId}/donate`, {
        donationDate,
      });
    } catch (error) {
      console.error("❌ Error updating donation status:", error);
    }
  };

  // Check if 3 months have passed since donation
  const isDonationLocked = (donationDate) => {
    if (!donationDate) return false;

    const donationTime = new Date(donationDate).getTime();
    const currentTime = new Date().getTime();
    const diffInDays = (currentTime - donationTime) / (1000 * 60 * 60 * 24);

    return diffInDays < 90; // Disable button if donation was within the last 90 days
  };

  // Filter donors based on search input
  const filteredDonors = donors.filter(
    (donor) =>
      (!searchCity ||
        (donor.city && donor.city.toLowerCase().includes(searchCity.toLowerCase()))) &&
      (!searchBloodType || donor.bloodType === searchBloodType)
  );

  return (
    <div className="dashboard-container">
      <div className="page-switch">
        <button onClick={() => setCurrentPage(0)} className={currentPage === 0 ? "active" : ""}>
          Become a Donor
        </button>
        <button onClick={() => setCurrentPage(1)} className={currentPage === 1 ? "active" : ""}>
          Find a Donor
        </button>
      </div>

      <div className="pages" style={{ transform: `translateX(-${currentPage * 100}%)` }}>
        {/* Page 1 - Become a Donor & Chat */}
        <div className="page">
          <h2>🩸 Become a Blood Donor ❤️</h2>

          {/* Donor Registration Form */}
          <form className="donor-form" onSubmit={handleRegisterDonor}>
            <input
              type="text"
              placeholder="Full Name"
              value={newDonor.name}
              onChange={(e) => setNewDonor({ ...newDonor, name: e.target.value })}
              required
            />
            <select
              value={newDonor.bloodType}
              onChange={(e) => setNewDonor({ ...newDonor, bloodType: e.target.value })}
              required
            >
              <option value="">Select Blood Type</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
            <input
              type="text"
              placeholder="City"
              value={newDonor.city}
              onChange={(e) => setNewDonor({ ...newDonor, city: e.target.value })}
              required
            />
            <input
              type="text"
              placeholder="Contact Number"
              value={newDonor.contact}
              onChange={(e) => setNewDonor({ ...newDonor, contact: e.target.value })}
              required
            />
            <button type="submit">Register as Donor</button>
          </form>

          <button className="chat-toggle-btn" onClick={() => setChatOpen(!chatOpen)}>
            {chatOpen ? "Close Chat" : "Open Chat"}
          </button>
          {chatOpen && <Chatbot />}
        </div>

        {/* Page 2 - Find a Donor */}
        <div className="page">
          <h3>🔍 Find a Blood Donor</h3>
          <input type="text" placeholder="Enter City" onChange={(e) => setSearchCity(e.target.value)} />
          <select onChange={(e) => setSearchBloodType(e.target.value)}>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>

          <div className="donor-list">
            {filteredDonors.map((donor) => (
              <div className="donor-card" key={donor._id}>
                <p>📍 {donor.city} | 🩸 {donor.bloodType} | 📞 {donor.contact} | 👤 {donor.name}</p>
                <button
                  className={"donate-btn " + (isDonationLocked(donor.donationDate) ? "donated" : "")}
                  onClick={() => handleDonateClick(donor._id)}
                  disabled={isDonationLocked(donor.donationDate)}
                >
                  {isDonationLocked(donor.donationDate) ? "Donated" : "Donate Now"}
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
