import React, { useState, useEffect } from "react";
import axios from "axios";
import ChatComponent from "../components/ChatComponent";
import Chatbot from "../components/Chatbot";
import "./Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");
  const [chatVisible, setChatVisible] = useState(false);

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/donors");
      setDonors(response.data);
    } catch (error) {
      console.error("Error fetching donors:", error);
    }
  };

  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact,
      });
      alert("‚úÖ Donor registered successfully!");
      setDonors([...donors, response.data.donor]);
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("Error adding donor:", error);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-content">
        {/* Left Section - Register as a Donor */}
        <div className="left-section">
          <h2>Become a Blood Donor ü©∏</h2>
          <form onSubmit={handleDonorSubmit} className="donor-form">
            <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} required />
            <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
              <option value="">Select Blood Type</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
            <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} required />
            <input type="text" placeholder="Contact" value={contact} onChange={(e) => setContact(e.target.value)} required />
            <button type="submit">Register</button>
          </form>
          <button className="chat-toggle" onClick={() => setChatVisible(!chatVisible)}>
            {chatVisible ? "Hide Chat" : "Open Chat"}
          </button>
          {chatVisible && <ChatComponent />}
          <Chatbot />
        </div>

        {/* Right Section - Find a Donor */}
        <div className="right-section">
          <h2>Find a Blood Donor üè•</h2>
          <input type="text" placeholder="Enter City" onChange={(e) => setSearchCity(e.target.value)} />
          <select onChange={(e) => setSearchBloodType(e.target.value)}>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>

          <h3>Available Donors</h3>
          <div className="donor-list">
            {donors.length === 0 ? (
              <p>No donors available.</p>
            ) : (
              donors.map((donor) => (
                <div className="donor-card" key={donor._id}>
                  <p><strong>{donor.name}</strong> | ü©∏ {donor.bloodType} | üìç {donor.city} | üìû {donor.contact}</p>
                  <button className="donate-btn">Donate Now</button>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
