import React, { useState } from "react";

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { text: "Hello! Ask me anything about blood donation.", sender: "bot" },
  ]);
  const [input, setInput] = useState("");

  const faqResponses = {
    "who can donate blood?":
      "Anyone aged 18-65, healthy, and weighing over 50kg can donate.",
    "how often can i donate?":
      "You can donate whole blood every 12 weeks (men) and every 16 weeks (women).",
    "does donating blood hurt?":
      "No, just a small pinch when the needle is inserted.",
    "how much blood is taken?":
      "About 470ml, which your body replaces within 24-48 hours.",
    "what are the benefits of donating blood?":
      "Blood donation helps save lives, reduces harmful iron stores, and stimulates the production of new blood cells.",
    "what should i do before donating blood?":
      "Drink plenty of water, eat a healthy meal, and avoid alcohol and smoking before donating.",
    "what should i do after donating blood?":
      "Rest for a few minutes, drink fluids, and avoid strenuous activities for the rest of the day.",
    "can i donate blood if i have diabetes?":
      "Yes, if your diabetes is under control and you're not on insulin injections.",
    "can i donate blood if i have high blood pressure?":
      "Yes, if your blood pressure is within a safe range at the time of donation.",
    "can pregnant women donate blood?":
      "No, pregnant women are not eligible to donate blood.",
    "how long does a blood donation take?":
      "The donation process takes about 10-15 minutes, but the entire visit may take about an hour.",
    "can i donate if i have a tattoo?":
      "Yes, but only if your tattoo was done in a licensed facility and is older than 6 months.",
    "what are the different blood donation types?":
      "There are whole blood, platelet, plasma, and double red cell donations.",
    "what is the universal donor blood type?":
      "O-negative blood can be given to patients of any blood type in emergencies.",
    "how can i check my blood type?":
      "You can check your blood type by donating blood or getting a blood test from a medical facility.",
    "where can i donate blood?":
      "You can donate at blood banks, hospitals, or mobile blood donation camps.",
  };

  const handleSendMessage = () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages([...messages, userMessage]);

    const response =
      faqResponses[input.toLowerCase()] ||
      "I'm not sure. Please contact a medical expert for more details.";
    setTimeout(() => {
      setMessages((prev) => [...prev, { text: response, sender: "bot" }]);
    }, 1000);

    setInput("");
  };

  return (
    <div>
      <button onClick={() => setIsOpen(!isOpen)} className="chat-toggle-btn">
        ðŸ’¬ Chat
      </button>

      {isOpen && (
        <div className="chatbot-container">
          <div className="chat-header">Blood Donation Chatbot</div>
          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div key={index} className={`chat-message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              placeholder="Ask about blood donation..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button onClick={handleSendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
