import React, { useState } from "react";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([
    { id: 1, name: "John Doe", bloodType: "A+", city: "New York", available: true, donated: false },
    { id: 2, name: "Jane Smith", bloodType: "O-", city: "Los Angeles", available: true, donated: false },
  ]);

  const handleDonateClick = (id) => {
    setDonors(donors.map(donor => donor.id === id ? { ...donor, donated: true, available: false } : donor));
  };

  return (
    <div className="dashboard-container">
      {/* Fixed Become a Blood Donor Section */}
      <div className="become-donor-container">
        <h2>Become a Blood Donor ‚ù§Ô∏è</h2>
        <input type="text" placeholder="Full Name" />
        <select><option>Select Blood Type</option></select>
        <input type="text" placeholder="City" />
        <input type="text" placeholder="Contact Number" />
        <button>Register</button>
      </div>

      {/* Find a Blood Donor (Scrollable) */}
      <div className="scroll-container">
        {donors.map((donor) => (
          <div key={donor.id} className="donor-card">
            <p>{donor.name} | {donor.bloodType} | {donor.city}</p>
            <p>{donor.available ? "‚úÖ Available" : "‚ùå Unavailable"}</p>
            {donor.donated ? (
              <button className="donated-text">Donated</button>
            ) : (
              <button className="donate-btn" onClick={() => handleDonateClick(donor.id)}>Donate Now</button>
            )}
          </div>
        ))}
      </div>

      {/* Fixed Chat Button */}
      <div className="chat-container">
        <button>Chat üí¨</button>
      </div>
    </div>
  );
};

export default Dashboard;
