import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import ChatComponent from "../components/ChatComponent";
import Chatbot from "../components/Chatbot";
import "./Dashboard.css"; // Ensure CSS is properly linked

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");
  const [showChat, setShowChat] = useState(false);
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/donors", {
        headers: { "Cache-Control": "no-cache" },
      });
      setDonors(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching donors:", error);
    }
  };

  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact,
      });
      alert("‚úÖ Donor added successfully!");
      setDonors([...donors, response.data.donor]);
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("‚ùå Donor registration failed:", error);
    }
  };

  const markAsDonated = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/donors/${id}/donate`);
      setDonors(donors.map((donor) => (donor._id === id ? { ...donor, donated: true, available: false } : donor)));
    } catch (error) {
      console.error("‚ùå Error marking donation:", error);
    }
  };

  const toggleAvailability = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/donors/${id}/toggle-availability`);
      setDonors(donors.map((donor) => (donor._id === id ? { ...donor, available: !donor.available } : donor)));
    } catch (error) {
      console.error("‚ùå Error toggling availability:", error);
    }
  };

  const filteredDonors = donors.filter(
    (donor) =>
      (!searchCity || donor.city.toLowerCase().includes(searchCity.toLowerCase())) &&
      (!searchBloodType || donor.bloodType === searchBloodType)
  );

  return (
    <div className="dashboard-container">
      <h2>ü©∏ Blood Donor Dashboard</h2>
      
      {/* Become a Donor Section */}
      <div className="section">
        <h3>Become a Blood Donor</h3>
        <form onSubmit={handleDonorSubmit}>
          <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} required />
          <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
          <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} required />
          <input type="text" placeholder="Contact Number" value={contact} onChange={(e) => setContact(e.target.value)} required />
          <button type="submit">Register</button>
        </form>
      </div>

      {/* Find a Donor Section */}
      <div className="section">
        <h3>Find a Blood Donor</h3>
        <input type="text" placeholder="Enter City" onChange={(e) => setSearchCity(e.target.value)} />
        <select onChange={(e) => setSearchBloodType(e.target.value)}>
          <option value="">Select Blood Type</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
        </select>
      </div>

      {/* Available Donors Section with Horizontal Scrolling */}
      <div className="scroll-container">
        {filteredDonors.map((donor) => (
          <div className="donor-card" key={donor._id}>
            <p>üìç {donor.city} | ü©∏ {donor.bloodType} | üìû {donor.contact} | üë§ {donor.name}</p>
            <button onClick={() => toggleAvailability(donor._id)}>
              {donor.available ? "üü¢ Available" : "üî¥ Unavailable"}
            </button>
            {!donor.donated ? (
              <button className="donate-btn" onClick={() => markAsDonated(donor._id)}>Donate Now</button>
            ) : (
              <span className="donated-text">‚úÖ Donated</span>
            )}
          </div>
        ))}
      </div>

      {/* Chatbot & Chat Section */}
      <button onClick={() => setShowChat(!showChat)}>üí¨ Toggle Chat</button>
      {showChat && <ChatComponent />}
      <Chatbot />
    </div>
  );
};

export default Dashboard;
