import React from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  return (
    <div>
      <h2>Welcome to the Blood Donation Dashboard</h2>

      {/* ‚úÖ Keep "Become a Donor" and "Find a Donor" Sections */}
      <div>
        <button onClick={() => navigate("/register")}>ü©∏ Become a Donor</button>
        <button onClick={() => navigate("/find-donor")}>üîç Find a Donor</button>
      </div>

      {/* ‚úÖ Show Donor Information Section (If applicable) */}
      <div>
        <h3>Donor Information</h3>
        <p>View available donors and their details.</p>
      </div>

      {/* ‚úÖ Always Show Chat Button */}
      <button onClick={() => navigate("/chat")}>üí¨ Open Live Chat</button>
    </div>
  );
};

export default Dashboard;
