import React, { useState } from "react";

const DonorForm = ({ onDonorAdded }) => {
  const [formData, setFormData] = useState({
    name: "",
    bloodType: "O+",
    city: "",
    contact: "",
  });

  const [errorMessage, setErrorMessage] = useState(""); // For displaying errors

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrorMessage(""); // Clear error message on change
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ✅ Step 1: Validate Required Fields
    if (!formData.name || !formData.bloodType || !formData.city || !formData.contact) {
      setErrorMessage("All fields are required.");
      return;
    }

    // ✅ Step 2: Validate Contact Number (Ensure it is at least 10 digits)
    if (!/^\d{10,}$/.test(formData.contact)) {
      setErrorMessage("Contact number must be at least 10 digits.");
      return;
    }

    try {
      // ✅ Step 3: Send Data to Backend
      const response = await fetch("http://localhost:5000/api/donors", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setFormData({ name: "", bloodType: "O+", city: "", contact: "" }); // Reset form
        setErrorMessage(""); // Clear errors
        alert("✅ Donor added successfully!");

        if (onDonorAdded) {
          onDonorAdded(); // Refresh donor list in parent component
        }
      } else {
        setErrorMessage(`❌ Failed to add donor: ${data.message}`);
      }
    } catch (error) {
      console.error("❌ Error submitting form:", error);
      setErrorMessage("Error adding donor. Ensure the backend is running and CORS is enabled.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Become a Blood Donor</h3>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>} {/* Show error message */}

      <input
        type="text"
        name="name"
        placeholder="Full Name"
        value={formData.name}
        onChange={handleChange}
        required
      />

      <select name="bloodType" value={formData.bloodType} onChange={handleChange}>
        <option value="O+">O+</option>
        <option value="A+">A+</option>
        <option value="B+">B+</option>
        <option value="AB+">AB+</option>
      </select>

      <input
        type="text"
        name="city"
        placeholder="City"
        value={formData.city}
        onChange={handleChange}
        required
      />

      <input
        type="text"
        name="contact"
        placeholder="Contact Number"
        value={formData.contact}
        onChange={handleChange}
        required
      />

      <button type="submit">Submit</button>
    </form>
  );
};

export default DonorForm;
