import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [filter, setFilter] = useState({ bloodType: "", city: "" });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/donors");
      setDonors(response.data);
    } catch (error) {
      console.error("Error fetching donors:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleFilterChange = (e) => {
    setFilter({ ...filter, [e.target.name]: e.target.value });
  };

  const filteredDonors = donors.filter((donor) =>
    (filter.bloodType ? donor.bloodType === filter.bloodType : true) &&
    (filter.city ? donor.city.toLowerCase().includes(filter.city.toLowerCase()) : true)
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>ü©∏ Available Blood Donors</h2>

      {/* Filter Section */}
      <div>
        <select name="bloodType" onChange={handleFilterChange}>
          <option value="">All Blood Types</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
        </select>

        <input
          type="text"
          name="city"
          placeholder="Search by city"
          onChange={handleFilterChange}
        />
      </div>

      {/* Donor List */}
      <div>
        {filteredDonors.length > 0 ? (
          filteredDonors.map((donor) => (
            <div key={donor._id} style={{ border: "1px solid gray", padding: "10px", margin: "10px 0" }}>
              <h3>{donor.name} ({donor.bloodType})</h3>
              <p>üìç {donor.city}</p>
              <p>üìû {donor.contact}</p>
              <button onClick={() => alert(`Request sent to ${donor.name}`)}>Request Blood</button>
            </div>
          ))
        ) : (
          <p>No donors available.</p>
        )}
      </div>

      <button onClick={handleLogout} style={{ marginTop: "20px" }}>Logout</button>
    </div>
  );
};

export default Dashboard;
