import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/donors")
      .then((response) => setDonors(response.data))
      .catch((error) => console.error("Error fetching donors:", error));
  }, []);

  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact,
      });
      alert("‚úÖ Donor added successfully!");
      setDonors([...donors, response.data.donor]);
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("‚ùå Donor registration failed:", error);
    }
  };

  return (
    <div className="dashboard-container">
      {/* Section 1: Register as Donor */}
      <section id="register" className="section">
        <h2>ü©∏ Become a Donor</h2>
        <form onSubmit={handleDonorSubmit}>
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
          <input
            type="text"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
          <button type="submit">Register</button>
        </form>
        {/* Live Chat Icon */}
        <div className="chat-icon">
          üí¨ Chat
        </div>
      </section>

      {/* Section 2: Find a Donor */}
      <section id="find" className="section">
        <h2>üîç Find a Donor</h2>
        <input type="text" placeholder="Enter City" />
        <select>
          <option value="">Select Blood Type</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
        </select>

        {/* Donor Posts */}
        <div className="donor-list">
          {donors.map((donor) => (
            <div key={donor._id} className="donor-card">
              <p>
                üìç {donor.city} | ü©∏ {donor.bloodType} | üìû {donor.contact} | üë§{" "}
                {donor.name}
              </p>
              <button className="donate-btn">Donate Now</button>
            </div>
          ))}
        </div>
      </section>

      {/* Navigation Buttons */}
      <div className="nav-buttons">
        <button onClick={() => document.getElementById("register").scrollIntoView({ behavior: "smooth" })}>
          ‚¨ÖÔ∏è Register
        </button>
        <button onClick={() => document.getElementById("find").scrollIntoView({ behavior: "smooth" })}>
          ‚û°Ô∏è Find Donor
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
