import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");

  // Fetch donors when the component mounts
  useEffect(() => {
    const fetchDonors = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/donors");
        setDonors(response.data);
      } catch (error) {
        console.error("❌ Error fetching donors:", error);
      }
    };

    fetchDonors();
  }, []);

  // Register as a donor
  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact
      });

      alert("✅ Donor added successfully!");

      // 🔹 Add new donor directly to state instead of refetching everything
      setDonors((prevDonors) => [...prevDonors, response.data.donor]);

      // Clear form
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("❌ Donor registration failed:", error);
    }
  };

  // Filter donors based on search criteria
  const filteredDonors = donors.filter(
    (donor) =>
      (!searchCity || donor.city.toLowerCase().includes(searchCity.toLowerCase())) &&
      (!searchBloodType || donor.bloodType === searchBloodType)
  );

  return (
    <div>
      <h2>🩸 Blood Donor Dashboard</h2>

      {/* Donor Registration Section */}
      <div>
        <h3>Become a Donor</h3>
        <form onSubmit={handleDonorSubmit}>
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="O+">O+</option>
          </select>
          <input
            type="text"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>

      {/* Receiver Section - Search Donors */}
      <div>
        <h3>Find a Donor</h3>
        <input
          type="text"
          placeholder="Enter City"
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <select onChange={(e) => setSearchBloodType(e.target.value)}>
          <option value="">Select Blood Type</option>
          <option value="A+">A+</option>
          <option value="O+">O+</option>
        </select>
      </div>

      {/* Donor List */}
      <div>
        <h3>Available Donors</h3>
        {filteredDonors.length === 0 ? (
          <p>No donors available.</p>
        ) : (
          filteredDonors.map((donor, index) => (
            <div key={index}>
              <p>📍 {donor.city} | 🩸 {donor.bloodType} | 📞 {donor.contact} | 👤 {donor.name}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Dashboard;
