import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../services/api";
import "./styles.css";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(formData);
      localStorage.setItem("token", response.token);
      navigate("/dashboard");
    } catch (err) {
      setError("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="auth-container">
      {/* Background styling to match register.js */}
      <div className="auth-background"></div>
      
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-md-8 col-lg-6">
            <div className="card shadow-lg border-0 transition-card">
              <div className="card-body p-5">
                <div className="text-center mb-4">
                  <i className="fas fa-heart-pulse fa-3x text-danger mb-3"></i>
                  <h2 className="fw-bold">Login to LifeDrop</h2>
                  <p className="text-muted">Save lives with your donation</p>
                </div>

                {error && (
                  <div className="alert alert-danger text-center">{error}</div>
                )}

                <form onSubmit={handleSubmit}>
                  <div className="mb-3 position-relative">
                    <i className="fas fa-envelope position-absolute top-50 translate-middle-y ms-3 text-danger"></i>
                    <input
                      type="email"
                      className="form-control ps-5"
                      name="email"
                      placeholder="Email Address"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="mb-4 position-relative">
                    <i className="fas fa-lock position-absolute top-50 translate-middle-y ms-3 text-danger"></i>
                    <input
                      type="password"
                      className="form-control ps-5"
                      name="password"
                      placeholder="Password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="d-flex justify-content-between mb-4">
                    <div className="form-check">
                      <input className="form-check-input" type="checkbox" id="rememberMe" />
                      <label className="form-check-label" htmlFor="rememberMe">
                        Remember me
                      </label>
                    </div>
                    <a href="#" className="text-danger small">Forgot password?</a>
                  </div>

                  <button type="submit" className="btn btn-danger w-100 py-2 transition-btn">
                    <i className="fas fa-sign-in-alt me-2"></i> Login
                  </button>

                  <div className="text-center mt-4">
                    <p className="mb-0">
                      Don't have an account?{" "}
                      <a href="/register" className="text-danger fw-bold">
                        Register
                      </a>
                    </p>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>

        {/* Added Contact Information Section */}
        <div className="row justify-content-center mt-5">
          <div className="col-md-8 col-lg-6">
            <div className="card shadow-lg border-0 transition-card">
              <div className="card-body p-5 text-center">
                <h3 className="fw-bold mb-4">Have questions? We're here to help!</h3>
                <button className="btn btn-outline-danger mb-4 transition-btn">
                  Contact Us
                </button>
                
                <div className="text-start mb-4">
                  <h5 className="fw-bold">123 Blood Bank Street, City</h5>
                  <h5 className="fw-bold">+1 234 567 890</h5>
                  <h5 className="fw-bold">contact@lifedrop.com</h5>
                </div>
                
                <div className="social-icons">
                  <h5 className="fw-bold mb-3">Follow Us</h5>
                  <div className="d-flex justify-content-center">
                    <a href="#" className="text-danger mx-3"><i className="fab fa-facebook-f fa-2x"></i></a>
                    <a href="#" className="text-danger mx-3"><i className="fab fa-twitter fa-2x"></i></a>
                    <a href="#" className="text-danger mx-3"><i className="fab fa-instagram fa-2x"></i></a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
