import React, { useState } from "react";
import Chat from "../components/Chat";

const Dashboard = () => {
  const user = JSON.parse(localStorage.getItem("user")); // Assuming you store user data in localStorage
  const [showChat, setShowChat] = useState(false); // Chat toggle state

  // Check if user is logged in and has a valid role
  const isAuthenticated = user && (user.role === "donor" || user.role === "recipient");

  return (
    <div>
      <h2>Welcome to the Blood Donation Dashboard</h2>

      {/* âœ… Chat Toggle Button */}
      {isAuthenticated ? (
        <>
          <button onClick={() => setShowChat(!showChat)}>
            {showChat ? "ðŸ”½ Hide Chat" : "ðŸ’¬ Open Live Chat"}
          </button>
          {showChat && <Chat />}
        </>
      ) : (
        <p>ðŸ”’ Log in as a donor or recipient to access live chat.</p>
      )}
    </div>
  );
};

export default Dashboard;
