import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
    const [donors, setDonors] = useState([]);
    const [filteredDonors, setFilteredDonors] = useState([]);
    const [searchCity, setSearchCity] = useState("");
    const [searchBloodType, setSearchBloodType] = useState("");
    const [activePage, setActivePage] = useState(0);
    
    useEffect(() => {
        fetch("/api/donors")
            .then((res) => res.json())
            .then((data) => {
                setDonors(data);
                setFilteredDonors(data);
            });
    }, []);

    useEffect(() => {
        const filtered = donors.filter((donor) =>
            donor.city.toLowerCase().includes(searchCity.toLowerCase()) &&
            donor.bloodType.toLowerCase().includes(searchBloodType.toLowerCase())
        );
        setFilteredDonors(filtered);
    }, [searchCity, searchBloodType, donors]);

    const toggleAvailability = (id) => {
        setDonors((prev) =>
            prev.map((donor) =>
                donor.id === id ? { ...donor, available: !donor.available } : donor
            )
        );
    };

    const handleDonate = (id) => {
        setDonors((prev) =>
            prev.map((donor) =>
                donor.id === id ? { ...donor, donated: true } : donor
            )
        );
    };

    return (
        <div className="dashboard">
            <div className="scroll-container" style={{ transform: `translateX(-${activePage * 100}%)` }}>
                {/* Page 1 - Become a Donor & Chat */}
                <div className="page">
                    <h2>Become a Blood Donor</h2>
                    <input type="text" placeholder="Search by City" onChange={(e) => setSearchCity(e.target.value)} />
                    <input type="text" placeholder="Search by Blood Type" onChange={(e) => setSearchBloodType(e.target.value)} />
                    <button onClick={() => setActivePage(1)}>Find a Donor</button>
                    <div className="chatbox">Chat Functionality Here</div>
                </div>

                {/* Page 2 - Find a Donor & Donor Info */}
                <div className="page">
                    <h2>Find a Donor</h2>
                    <button onClick={() => setActivePage(0)}>Back</button>
                    <div className="donor-list">
                        {filteredDonors.map((donor) => (
                            <div key={donor.id} className="donor-card">
                                <p>{donor.name} | {donor.bloodType} | {donor.city}</p>
                                <button onClick={() => toggleAvailability(donor.id)}>
                                    {donor.available ? "Available" : "Unavailable"}
                                </button>
                                <button disabled={donor.donated} onClick={() => handleDonate(donor.id)}>
                                    {donor.donated ? "Donated" : "Donate Now"}
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
