import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import ChatComponent from "../components/ChatComponent";
import Chatbot from "../components/Chatbot";
import "./Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");
  const [chatVisible, setChatVisible] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/donors");
      setDonors(response.data);
    } catch (error) {
      console.error("❌ Error fetching donors:", error);
    }
  };

  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact,
      });

      alert("✅ Donor added successfully!");
      setDonors([...donors, response.data.donor]);
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("❌ Donor registration failed:", error);
    }
  };

  const markAsDonated = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/donors/${id}/donate`);
      setDonors(
        donors.map((donor) =>
          donor._id === id ? { ...donor, donated: true, available: false } : donor
        )
      );
    } catch (error) {
      console.error("❌ Error marking donation:", error);
    }
  };

  const toggleAvailability = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/donors/${id}/toggle-availability`);
      setDonors(
        donors.map((donor) =>
          donor._id === id ? { ...donor, available: !donor.available } : donor
        )
      );
    } catch (error) {
      console.error("❌ Error toggling availability:", error);
    }
  };

  const filteredDonors = donors.filter(
    (donor) =>
      (!searchCity || donor.city.toLowerCase().includes(searchCity.toLowerCase())) &&
      (!searchBloodType || donor.bloodType === searchBloodType)
  );

  return (
    <div className="dashboard-container">
      {/* Sidebar Navigation */}
      <div className="sidebar">
        <h2>🩸 Blood Donor Dashboard</h2>
        <button onClick={() => navigate("/")}>🏠 Home</button>
        <button onClick={() => setChatVisible(!chatVisible)}>
          {chatVisible ? "Hide Chat" : "💬 Open Chat"}
        </button>
        <button onClick={() => navigate("/requests")}>🔍 Blood Requests</button>
      </div>

      {/* Main Content Area */}
      <div className="main-content">
        <div className="message-area">
          <h3>Find a Donor</h3>
          <input type="text" placeholder="Enter City" onChange={(e) => setSearchCity(e.target.value)} />
          <select onChange={(e) => setSearchBloodType(e.target.value)}>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>

          <h3>Available Donors</h3>
          <div className="donor-list">
            {filteredDonors.length === 0 ? (
              <p>No donors available.</p>
            ) : (
              filteredDonors.map((donor) => (
                <div className="donor-card" key={donor._id}>
                  <p>📍 {donor.city} | 🩸 {donor.bloodType} | 📞 {donor.contact} | 👤 {donor.name}</p>
                  <button onClick={() => toggleAvailability(donor._id)}>
                    {donor.available ? "🟢 Available" : "🔴 Unavailable"}
                  </button>
                  {!donor.donated ? (
                    <button className="donate-btn" onClick={() => markAsDonated(donor._id)}>
                      Donate Now
                    </button>
                  ) : (
                    <span className="donated-text">✅ Donated</span>
                  )}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Chat Section */}
        {chatVisible && <ChatComponent />}
        <Chatbot />
      </div>
    </div>
  );
};

export default Dashboard;
