import React, { useState, useEffect } from "react";
import axios from "axios";
import ChatComponent from "../components/ChatComponent";
import Chatbot from "../components/Chatbot";
import "./Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");
  const [currentPage, setCurrentPage] = useState(0);
  const [showChat, setShowChat] = useState(true); // Chat visibility toggle

  useEffect(() => {
    fetchDonors();
  }, []);

  const fetchDonors = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/donors", {
        headers: { "Cache-Control": "no-cache" },
      });
      setDonors(response.data);
    } catch (error) {
      console.error("❌ Error fetching donors:", error);
    }
  };

  const handleRegisterDonor = async (e) => {
    e.preventDefault();
    if (!name || !bloodType || !city || !contact) {
      alert("❌ Please fill out all fields.");
      return;
    }

    try {
      const newDonor = { name, bloodType, city, contact, available: true, donated: false };
      const response = await axios.post("http://localhost:5000/api/donors", newDonor);
      setDonors([...donors, response.data]);
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
      alert("✅ Donor registered successfully!");
    } catch (error) {
      console.error("❌ Error registering donor:", error);
      alert("❌ Failed to register donor. Please try again.");
    }
  };

  const handleDonateClick = (donorId) => {
    setDonors((prevDonors) =>
      prevDonors.map((donor) =>
        donor._id === donorId ? { ...donor, donated: true } : donor
      )
    );
  };

  const toggleAvailability = (donorId) => {
    setDonors((prevDonors) =>
      prevDonors.map((donor) =>
        donor._id === donorId ? { ...donor, available: !donor.available } : donor
      )
    );
  };

  const filteredDonors = donors.filter(
    (donor) =>
      (!searchCity || (donor.city && donor.city.toLowerCase().includes(searchCity.toLowerCase()))) &&
      (!searchBloodType || donor.bloodType === searchBloodType)
  );

  return (
    <div className="dashboard-container">
      <div className="page-switch">
        <button onClick={() => setCurrentPage(0)} className={currentPage === 0 ? "active" : ""}>Become a Donor</button>
        <button onClick={() => setCurrentPage(1)} className={currentPage === 1 ? "active" : ""}>Find a Donor</button>
      </div>
      
      <div className="pages" style={{ transform: `translateX(-${currentPage * 100}%)` }}>
        {/* Page 1 - Become a Donor & Chat */}
        <div className="page">
          <h2>🩸 Become a Blood Donor ❤️</h2>
          <form className="donor-form" onSubmit={handleRegisterDonor}>
            <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} required />
            <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
              <option value="">Select Blood Type</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
            <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} required />
            <input type="text" placeholder="Contact Number" value={contact} onChange={(e) => setContact(e.target.value)} required />
            <button type="submit">Register</button>
          </form>

          {/* Chat Section */}
          <div className="chat-container">
            <button className="chat-toggle-btn" onClick={() => setShowChat(!showChat)}>
              {showChat ? "Hide Chat" : "Show Chat"}
            </button>
            {showChat && (
              <>
                <ChatComponent />
                <Chatbot />
              </>
            )}
          </div>
        </div>

        {/* Page 2 - Find a Donor */}
        <div className="page">
          <h3>🔍 Find a Blood Donor</h3>
          <div className="search-filters">
            <input type="text" placeholder="Enter City" onChange={(e) => setSearchCity(e.target.value)} />
            <select onChange={(e) => setSearchBloodType(e.target.value)}>
              <option value="">Select Blood Type</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>

          <div className="donor-list-container">
            <div className="donor-list">
              {filteredDonors.length > 0 ? (
                filteredDonors.map((donor) => (
                  <div className="donor-card" key={donor._id}>
                    <p>📍 {donor.city} | 🩸 {donor.bloodType} | 📞 {donor.contact} | 👤 {donor.name}</p>
                    <button 
                      className={`availability-btn ${donor.available ? "available" : "unavailable"}`}
                      onClick={() => toggleAvailability(donor._id)}
                    >
                      {donor.available ? "Available" : "Unavailable"}
                    </button>
                    <button 
                      className={`donate-btn ${donor.donated ? "donated" : ""}`} 
                      onClick={() => handleDonateClick(donor._id)}
                      disabled={donor.donated}
                    >
                      {donor.donated ? "Donated" : "Donate Now"}
                    </button>
                  </div>
                ))
              ) : (
                <p className="no-donors">No donors found for the selected criteria.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
