import React, { useState } from "react";

const DonorForm = ({ onDonorAdded }) => {
  const [formData, setFormData] = useState({
    name: "",
    bloodType: "O+",
    city: "",
    contact: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic Validation: Contact Number (Ensure it is at least 10 digits)
    if (formData.contact.length < 10) {
      alert("Contact number must be at least 10 digits.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/donors", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Donor added successfully!");
        setFormData({ name: "", bloodType: "O+", city: "", contact: "" });
        if (onDonorAdded) {
          onDonorAdded(); // Refresh donor list
        }
      } else {
        alert(`Failed to add donor: ${data.message}`);
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Error adding donor. Ensure the backend is running and CORS is enabled.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Become a Blood Donor</h3>
      <input type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} required />
      <select name="bloodType" value={formData.bloodType} onChange={handleChange}>
        <option value="O+">O+</option>
        <option value="A+">A+</option>
        <option value="B+">B+</option>
        <option value="AB+">AB+</option>
      </select>
      <input type="text" name="city" placeholder="City" value={formData.city} onChange={handleChange} required />
      <input type="text" name="contact" placeholder="Contact Number" value={formData.contact} onChange={handleChange} required />
      <button type="submit">Submit</button>
    </form>
  );
};

export default DonorForm;
