import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import "./ChatComponent.css";

const socket = io("http://localhost:5000"); // Adjust backend URL if needed

const ChatComponent = () => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on("message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => socket.off("message");
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("message", { text: message, sender: "user" });
      setMessages((prev) => [...prev, { text: message, sender: "user" }]);
      setMessage("");

      setTimeout(() => {
        setMessages((prev) => [...prev, { text: "Thank you for reaching out! How can I assist?", sender: "bot" }]);
      }, 1000);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">ðŸ’¬ Live Chat</div>
      <div className="chat-body">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-message ${msg.sender}`}>
            <span>{msg.text}</span>
          </div>
        ))}
        <div ref={messagesEndRef}></div>
      </div>
      <div className="chat-footer">
        <input
          type="text"
          placeholder="Type your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatComponent;
