import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css"; // Import the new CSS file

const Dashboard = () => {
  const [donors, setDonors] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/donors")
      .then((response) => setDonors(response.data))
      .catch((error) => console.error("‚ùå Error fetching donors:", error));
  }, []);

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-title">ü©∏ Blood Donors</h2>

      <div className="donor-list">
        {donors.length === 0 ? (
          <p className="no-donors">No donors available at the moment.</p>
        ) : (
          donors.map((donor) => (
            <div key={donor._id} className="donor-card">
              <p>
                üìç {donor.city} | ü©∏ {donor.bloodType} | üìû {donor.contact} | üë§ {donor.name}
              </p>
              <button className="donate-btn">Donate Now</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Dashboard;
