import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [isChatOpen, setIsChatOpen] = useState(false);
  const scrollRef = useRef(null);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/donors")
      .then((response) => setDonors(response.data))
      .catch((error) => console.error("Error fetching donors:", error));
  }, []);

  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact,
      });
      alert("✅ Donor added successfully!");
      setDonors([...donors, response.data.donor]);
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("❌ Donor registration failed:", error);
    }
  };

  const handleScroll = (direction) => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        left: direction === "right" ? window.innerWidth : -window.innerWidth,
        behavior: "smooth",
      });
    }
  };

  return (
    <div className="dashboard-container" ref={scrollRef}>
      {/* Section 1: Register as Donor */}
      <section id="register" className="section">
        <h2>🩸 Become a Donor</h2>
        <form onSubmit={handleDonorSubmit}>
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
          <input
            type="text"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
          <button type="submit">Register</button>
        </form>
      </section>

      {/* Section 2: Find a Donor */}
      <section id="find" className="section">
        <h2>🔍 Find a Donor</h2>
        <input type="text" placeholder="Enter City" />
        <select>
          <option value="">Select Blood Type</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
        </select>

        {/* Donor Posts */}
        <div className="donor-list">
          {donors.map((donor) => (
            <div key={donor._id} className="donor-card">
              <p>
                📍 {donor.city} | 🩸 {donor.bloodType} | 📞 {donor.contact} | 👤{" "}
                {donor.name}
              </p>
              <button className="donate-btn" onClick={() => alert("🩸 Donation process initiated!")}>
                Donate Now
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* Navigation Buttons */}
      <div className="nav-buttons">
        <button onClick={() => handleScroll("left")}>⬅️ Back</button>
        <button onClick={() => handleScroll("right")}>➡️ Next</button>
      </div>

      {/* Toggle Chat Button */}
      <div className="chat-icon" onClick={() => setIsChatOpen(!isChatOpen)}>💬</div>
      
      {isChatOpen && (
        <div className="chat-window">
          <h3>Live Chat</h3>
          <p>🗣 Chat feature coming soon...</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
