import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [organDonors, setOrganDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  const [organs, setOrgans] = useState([]);
  const [searchCity, setSearchCity] = useState("");
  const [searchBloodType, setSearchBloodType] = useState("");
  const [searchOrgan, setSearchOrgan] = useState("");

  // Fetch both blood & organ donors when the component mounts
  useEffect(() => {
    const fetchDonors = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/donors");
        setDonors(response.data);
      } catch (error) {
        console.error("❌ Error fetching donors:", error);
      }
    };

    const fetchOrganDonors = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/donors/organ-donors");
        setOrganDonors(response.data);
      } catch (error) {
        console.error("❌ Error fetching organ donors:", error);
      }
    };

    fetchDonors();
    fetchOrganDonors();
  }, []);

  // Register as a blood donor
  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors", {
        name,
        bloodType,
        city,
        contact,
      });

      alert("✅ Donor added successfully!");

      // Add new donor to state
      setDonors((prevDonors) => [...prevDonors, response.data.donor]);

      // Clear form
      setName("");
      setBloodType("");
      setCity("");
      setContact("");
    } catch (error) {
      console.error("❌ Donor registration failed:", error);
    }
  };

  // Register as an organ donor
  const handleOrganDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/donors/organ-donor", {
        name,
        city,
        contact,
        organs,
      });

      alert("✅ Organ donor registered successfully!");

      // Add new organ donor to state
      setOrganDonors((prevDonors) => [...prevDonors, response.data.donor]);

      // Clear form
      setName("");
      setCity("");
      setContact("");
      setOrgans([]);
    } catch (error) {
      console.error("❌ Organ donor registration failed:", error);
    }
  };

  // Mark a donor as donated
  const markAsDonated = async (id) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/donors/${id}/donated`);
      
      if (response.status === 200) {
        setDonors(donors.map(donor => 
          donor._id === id ? { ...donor, donated: true } : donor
        ));
        alert("✅ Donor marked as donated!");
      }
    } catch (error) {
      console.error("❌ Error updating donor status:", error);
      alert("❌ Failed to mark as donated.");
    }
  };

  // Filter blood donors based on search criteria
  const filteredDonors = donors.filter(
    (donor) =>
      donor &&
      (!searchCity || donor.city.toLowerCase().includes(searchCity.toLowerCase())) &&
      (!searchBloodType || donor.bloodType === searchBloodType)
  );

  // Filter organ donors based on search criteria
  const filteredOrganDonors = organDonors.filter(
    (donor) =>
      donor &&
      (!searchCity || donor.city.toLowerCase().includes(searchCity.toLowerCase())) &&
      (!searchOrgan || donor.organs.includes(searchOrgan))
  );

  return (
    <div>
      <h2>🩸 Blood & Organ Donor Dashboard</h2>

      {/* Blood Donor Registration Section */}
      <div>
        <h3>Register as a Blood Donor</h3>
        <form onSubmit={handleDonorSubmit}>
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
          <input
            type="text"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>

      {/* Organ Donor Registration Section */}
      <div>
        <h3>Register as an Organ Donor</h3>
        <form onSubmit={handleOrganDonorSubmit}>
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Organs (comma-separated, e.g., Kidney, Heart)"
            value={organs}
            onChange={(e) => setOrgans(e.target.value.split(","))}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>

      {/* Search Blood Donors */}
      <div>
        <h3>Find a Blood Donor</h3>
        <input
          type="text"
          placeholder="Enter City"
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <select onChange={(e) => setSearchBloodType(e.target.value)}>
          <option value="">Select Blood Type</option>
          <option value="A+">A+</option>
          <option value="A-">A-</option>
          <option value="B+">B+</option>
          <option value="B-">B-</option>
          <option value="AB+">AB+</option>
          <option value="AB-">AB-</option>
          <option value="O+">O+</option>
          <option value="O-">O-</option>
        </select>
      </div>

      {/* Display Organ Donors */}
      <div>
        <h3>Available Organ Donors</h3>
        {filteredOrganDonors.map((donor, index) => (
          <p key={index}>
            📍 {donor.city} | 👤 {donor.name} | 📞 {donor.contact} | 🏥 Organs: {donor.organs.join(", ")}
          </p>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
