import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [donors, setDonors] = useState([]);
  const [name, setName] = useState("");
  const [bloodType, setBloodType] = useState("");
  const [city, setCity] = useState("");
  const [contact, setContact] = useState("");
  
  // Fetch all donors
  useEffect(() => {
    axios.get("http://localhost:5000/api/donors")
      .then(response => setDonors(response.data))
      .catch(error => console.error("Error fetching donors:", error));
  }, []);

  // Register as a donor
  const handleDonorSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/donors", { name, bloodType, city, contact });
      alert("✅ Donor added successfully!");
      window.location.reload();
    } catch (error) {
      console.error("Donor registration failed:", error);
    }
  };

  return (
    <div>
      <h2>🩸 Blood Donor Dashboard</h2>
      
      {/* Donor Registration Section */}
      <div>
        <h3>Become a Donor</h3>
        <form onSubmit={handleDonorSubmit}>
          <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} required />
          <select value={bloodType} onChange={(e) => setBloodType(e.target.value)} required>
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="O+">O+</option>
          </select>
          <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} required />
          <input type="text" placeholder="Contact Number" value={contact} onChange={(e) => setContact(e.target.value)} required />
          <button type="submit">Submit</button>
        </form>
      </div>

      {/* Receiver Section */}
      <div>
        <h3>Find a Donor</h3>
        <input type="text" placeholder="Enter City" onChange={(e) => setCity(e.target.value)} />
        <select onChange={(e) => setBloodType(e.target.value)}>
          <option value="">Select Blood Type</option>
          <option value="A+">A+</option>
          <option value="O+">O+</option>
        </select>
        <button>Search</button>
      </div>

      {/* Donor List */}
      <div>
        <h3>Available Donors</h3>
        {donors.length === 0 ? <p>No donors available.</p> : donors.map((donor, index) => (
          <div key={index}>
            <p>📍 {donor.city} | 🩸 {donor.bloodType} | 📞 {donor.contact} | 👤 {donor.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
