import React, { useState, useEffect } from "react";
import DonorForm from "./DonorForm"; // Import donor form

const Dashboard = () => {
  const [donors, setDonors] = useState([]);

  const fetchDonors = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/donors");
      const data = await response.json();
      setDonors(data);
    } catch (error) {
      console.error("Error fetching donors:", error);
    }
  };

  useEffect(() => {
    fetchDonors();
  }, []);

  return (
    <div>
      <h2>ðŸ©¸ Available Blood Donors</h2>
      <DonorForm onDonorAdded={fetchDonors} /> {/* Add donor form */}
      {donors.length === 0 ? (
        <p>No donors available.</p>
      ) : (
        <ul>
          {donors.map((donor) => (
            <li key={donor._id}>
              {donor.name} - {donor.bloodType} - {donor.city} - {donor.contact}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Dashboard;
